function setLoginButton(){$.get("/php/login/isLoggedIn.php",function(a){if(a!="false"){console.log("Logged in");$(".user-loggedin").show();$("#user-login").hide()}else{console.log("Logged out");$(".user-loggedin").hide();$("#user-login").show()}})}function logout(){$.get("/php/login/logout.php",function(a){location.reload(true)})}window.addEventListener("load",function(a){setLoginButton()});