var time=document.getElementById("weather-update-time");var temp=document.getElementById("weather-temp");var dew=document.getElementById("weather-dew");var hum=document.getElementById("weather-hum");var pres=document.getElementById("weather-pres");var rain_hour=document.getElementById("weather-rain-hour");var rain_day=document.getElementById("weather-rain-day");var wind=document.getElementById("weather-wind");var wind_dir=document.getElementById("weather-wind-dir");var gust=document.getElementById("weather-gust");var gust_dir=document.getElementById("weather-gust-dir");var avg=document.getElementById("weather-avg");var avg_dir=document.getElementById("weather-avg-dir");function degToDir(b){var a=22.5;if(b<a){return"North"}else{if(b<a*3){return"North-East"}else{if(b<a*5){return"East"}else{if(b<a*7){return"South-East"}else{if(b<a*9){return"South"}else{if(b<a*11){return"South-West"}else{if(b<a*13){return"West"}else{if(b<a*15){return"North-West"}else{return"North"}}}}}}}}}function updateWeather(){$.get("/php/weather/weather-status.php",function(a){var b=JSON.parse(a);time.textContent=b.date;temp.textContent=b.temperature;dew.textContent=b.dewpoint;hum.textContent=b.humidity;pres.textContent=b.pressure;rain_hour.textContent=b.rain_hour;rain_day.textContent=b.rain_day;wind.textContent=b.wind_speed;wind_dir.textContent=degToDir(b.wind_dir);gust.textContent=b.wind_gust;gust_dir.textContent=degToDir(b.wind_gust_dir);avg.textContent=b.wind_avg;avg_dir.textContent=degToDir(b.wind_avg_dir)})}window.addEventListener("load",function(a){setInterval(updateWeather,30*1000);updateWeather()});